INSERT INTO feature (title, author, thumbnail, excerpt, publication, link, location) VALUES
('Kilimo kwanza - Tansanian maatalouden piristysruiske?', 'Toni Haapanen', NULL, 'Presidentti Jakaya Kikwete on painottanut, että Kilimo kwanza on keskeisessä roolissa YK:n vuosituhattavoitteiden saavuttamiseksi, ja että yksityissektorilla tulee olemaan ohjelman vetovastuu (The Guardian 2009). Siirtomaa-ajoilta ja sosialistiselta ujamaa-aikakaudelta valtion omistukseen jääneet plantaasit ja “joutomaat” on tarkoitus myydä yksityisyrittäjille, joiden odotetaan sijoittavan tehotuotantoon suurella volyymilla. Hallitus lupaa osaltaan panostaa keinokastelun lisäämiseen, maatalouskoneiden saatavuuteen, lannoitteiden ja torjunta-aineiden kotimaiseen tuotantoon, tuottoisampien lajikkeiden kehittämiseen, markkinointiin ja varastointiin sekä viljelijöiden koulutukseen (TNBC 2009a, 2009b).', 'Maapallo 01/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[35.7796,-6.1630]}'), 3067)),
('Ekomatkailun kehittämishanke Cat Ban saarella Pohjois-Vietnamissa', 'Jari Hietaranta', 'https://example.com/thumb2.jpg', 'Ympäristöasiat ovat aina olleet taka-alalla Vietnamissa Vietnamin sodan ja sitä seuranneen epävakaan poliittisen ilmapiirin vuoksi. Sota jakoi maan kahteen taloudellisesti ja poliittisesti eriytyneeseen ryhmään, ja tämä kahtiajako on nähtävissä edelleen. Vietnamia viime vuosikymmeninä kohdannut nopea väestönkasvu on peräänkuuluttanut merkittäviä taloudellisia muutoksia ja kasvattanut luonnonresursseihin kohdistuvia paineita.', 'Maapallo 02/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[37.2692,-13.2543]}'), 3067)),
('Wixarica-alkuperäiskansa puolustaa oikeuttaan määritellä itse tulevaisuutensa', 'Sara Peltonen', NULL, 'On harvinaista, että alkuperäiskansa voittaa oikeusteitse takaisin suuret määrät maitaan. Vielä harvinaisempaa on, että alkuperäiskansa saa itse määritellä miten maitaan hyödyntää.', 'Maapallo 3-4/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[-13.0033,25.0000]}'), 3067)),
('Kumppanuutta ja kehitysyhteistyötä: Lawra-projekti', 'Maria Viitasaari', 'https://example.com/thumb4.jpg', 'Helsingin yliopiston maatalous-metsäylioppilaiden liiton (MMYL ry) oma kehitysyhteistyöprojekti Lawra-projekti pyrkii parantamaan maaseudun asukkaiden toimeentulomahdollisuuksia Luoteis-Ghanassa. Samalla se antaa suomalaisille opiskelijoille mahdollisuuden ruohonjuuritason kehitysyhteistyöhön. Yli 20 vuotta jatkunut projekti perustuu kumppanuudelle ja molemminpuoliselle oppimiselle.', 'Maapallo 3-4/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[35.2033,31.9522]}'), 3067)),
('Den indiska kvinnan: vilja, mod och ihärdighet', 'Elina Andersson', NULL, 'Vad tänker du på då du hör talas om Indien? Kanske fattigdom, ett hett klimat, starka kryddor, färggranna festivaler, Mohandas Gandhi, hinduism, Taj Mahal... Men hur är det med de indiska kvinnorna, vad vet vi egentligen om dem här i västvärlden? Då jag själv åkte till Indien visste jag inte mycket - jag förknippade dem närmast med samma exotism och mysticism som hela den indiska kulturen ofta stereotypt stämplas som. I Indien kom jag att bli stort imponerad av de lokala kvinnorna. Här skall jag berätta varför.', 'Maapallo 3-4/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[121.0794,-2.1115]}'), 3067)),
('Vapaaehtoistyössä Sansibarin auringon alla', 'Elina Nieminen', 'https://example.com/thumb6.jpg', 'Matkustin Sansibarille Tansaniaan työharjoitteluun LIVE-kehitysyhteistyöprojektiin vuoden 2011 marraskuun alussa. Opiskelen maantiedettä Turun Yliopistossa kuudetta vuotta, ja sivuaineena olen opiskellut kehitysmaatutkimusta. Mahdollisuus kehitysmaatutkimuksen opiskeluun oli yksi niistä syistä, miksi valitsin Turun yliopiston opiskelupaikakseni.', 'Maapallo 3-4/2011', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[1.0000,15.0000]}'), 3067)),
('Rahalähetykset - yksi Ecuadorin kehityksen voimavaroista?', 'Meeri Väänänen', NULL, 'Muuttoliikkeet aiheuttavat päivittäistä keskustelua yhteiskunnan eri osa-alueilla. Usein ilmiöön liittyvä uutisointi on negatiivista muuttoliikkeiden tuodessa muutoksia niin lähettävän kuin vastaanottavan maan yhteiskuntarakenteisiin ja taloudellisiin painotuksiin. Varsinkin viime vuosikymmeninä muuttoliikkeet ovat saaneet entistä globaalimpia sävyjä ja toisaalta maailmanlaajuisempaa huomiota, sillä uusien teknologioiden ja välimatkojen ”lyhenemisen” myötä muuttajien on ollut entistä helpompi pitää yhteyttä entisissä kotimaissaan asuviin perheisiinsä. Tämä yhteydenpito on tuonut esille yhden muuttoliikkeiden näkyvistä ilmiöistä - rahalähetykset perheiden välillä.', 'Maapallo 1/2012', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[10.0000,30.0000]}'), 3067)),
('Tian Shan vuorten helmi - Issyk-Kul', 'Heimo Mikkola', 'https://example.com/thumb8.jpg', 'Issyk-Kul järvi Kirgisiassa on maailman toiseksi suurin vuoristojärvi Etelä-Amerikan Titicaca-järven jälkeen, ja syvyyden (702 m) suhteen se on viides kaikkien maailman järvien joukossa. Järveä on ylistetty Tian Shan vuorten helmeksi ja vuoria puolestaan taivaan vuoriksi. En paljon hienompaa näkyä osaa kuvitella, kun talvellakin sula 60 x 180 km järvi välkkyy sinisen eri sävyissä yli viiteen kilometriin nousevien lumen peittämien vuorten keskellä.', 'Maapallo 1/2012', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[120.0000,-1.0000]}'), 3067)),
('AIESEC ahertaa ruohonjuuritason kehitysprojektien parissa Ghanassa', 'Matias Andersson', NULL, 'Opiskelijajärjestöt ovat tunnetusti aktiivisia toimijoita monella yhteiskunnan taholla monissa maissa. Kumasissa, Ghanassa AIESEC (Association Internationale des Etudiants en Sciences Economiques et Commerciales) vetää kourallista pieniä ruohonjuuritason kehityshankkeita. Kaksi maisterivaiheen kehitysmaantieteen opiskelijaa Helsingin Yliopistosta tarttui tilaisuuteen suorittaa työharjoittelunsa kahdessa näistä projekteista, ja matkusti Kumasiin koko kesäksi. Kirjoittaja työskenteli nuorten yrittäjyyttä ja pienten yritysten toimintaa edistävässä hankkeessa. Esittelen lyhyesti järjestön ja sen vetämät hankkeet esimerkkinä pienimuotoisesta, vähin resurssein pyöritetystä kehitystyöstä.', 'Maapallo 2/2012', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[40.0000,-10.0000]}'), 3067)),
('Metsänistutuksen haasteita Himalajalla', 'Toni Haapanen', 'https://example.com/thumb10.jpg', 'Puhtaan kehityksen mekanismin (CDM) avulla pyritään torjumaan ilmastonmuutosta muun muassa metsiä istuttamalla. Vaikka metsittämisellä on todettu olevan huima potentiaali globaalisti, niin CDM:ään ja metsiin hiilinieluina liittyy myös monia ekologisia, sosiaalisia ja taloudellisia haasteita. Intian Himalajalla CDM-istutuksia on pyritty toteuttamaan uudenlaisella lähestymistavalla nämä haasteet ainakin osittain huomioiden. Tutkimuksessani pyrin selvittämään, miten tavoitteissa on onnistuttu ja millaisia vaikutuksia istutuksilla on ollut paikallisiin elinkeinoihin ja maankäyttöön.', 'Maapallo 1/2013', 'https://kehmy.fi/maapallo-lehti', ST_SetSRID(ST_GeomFromGeoJSON('{"type":"Point","coordinates":[150.0000,-20.0000]}'), 3067));
